mapper.insert:
INSERT INTO tb_writing(
    wid
    , uid
    , content
    , cdate
    , hit
    , likes
) VALUES (
    #{wid}
    , #{uid}
    , #{content}
    , NOW()
    , #{hit}
    , #{likes}
)

mapper.update:
UPDATE tb_writing
    SET
        content = #{content}
        , userId = #{userId}
        , udate = #{udate}
WHERE
    wid = #{wid}
