mapper.insert:
INSERT INTO tb_writing(
    wid
    , uid
    , contents
    , cdate
    , hit
    , likes
) VALUES (
    #{wid}
    , #{uid}
    , #{contents}
    , NOW()
    , #{hit}
    , #{likes}
)

mapper.update:
UPDATE tb_writing
    SET
        contents = #{contents}
        , userId = #{userId}
WHERE
    id = #{wid}
